import React, { Component } from 'react';
import memberInfo from './memberInfo';

class Team extends Component{

    constructor(){
        super()
        this.state = { 
            memberInfo : memberInfo,
            members: [],
            counter: 0,
         }

        this.clickEvent = this.clickEvent.bind(this);
    }

    clickEvent(){
        if(this.state.counter < this.state.memberInfo.length){
            this.state.members.push(this.state.memberInfo[this.state.counter]);
            this.setState(function(prevState){
                return { counter: prevState.counter + 1 }
            });
            this.newMembers = this.state.members.map(member => {
                return <TeamMember 
                        key={member.id}
                        img={member.img}
                        name={member.name}
                        position={member.position}
                        phone={member.phone}
                        email={member.email}
                        website={member.website}
                    />
            })
        }
    }

    render(){        
        return(
            <div className="row">
                <div className="col-md-12">
                    <button className="btn btn-primary btn-lg btn-block" 
                    onClick={this.clickHandler}>Click me!</button>
                </div>
                {this.newMembers}
            </div>
        )
    }
}

export default Team
